# 调用Spark的内置SVM算法识别手写数字
import os
from numpy import array
from pyspark import SparkContext
from pyspark.mllib.classification import SVMWithSGD
from pyspark.mllib.regression import LabeledPoint


def getDatas():
    datas = []
    dirs = os.listdir('trainingdigits')
    for name in dirs:
        num = [] 
        label = 1 if name.startswith('0') else 0
        lines = open('trainingdigits/' + name).readlines()
        for line in lines:
            for i in line.strip():
                num.append(int(i))
        datas.append((LabeledPoint(label, num)))
    return datas
    
if __name__ == '__main__':
    sc = SparkContext()
    datas = getDatas()
    rdd = sc.parallelize(datas)
    model = SVMWithSGD.train(rdd, iterations=100)
